import subprocess, sys, json
from pathlib import Path

# === Base Paths ===
BASE = Path(".").resolve()
SRC = BASE / "src"
TESTS = BASE / "tests"
TESTS.mkdir(exist_ok=True)

# === Utility to run shell commands ===
def sh(cmd, capture=False, check=True):
    print(f"$ {cmd}")
    result = subprocess.run(cmd, shell=True, text=True, capture_output=capture)
    if check and result.returncode != 0:
        print(result.stdout)
        print(result.stderr, file=sys.stderr)
        sys.exit(result.returncode)
    return result.stdout if capture else ""

# === Detect changed Python files ===
def get_changed_files():
    """
    Detects changed Python files between the PR branch (HEAD) and origin/main.
    Works even on shallow clones or brand-new PRs.
    """
    # Ensure we have 'main' branch reference locally
    subprocess.run(["git", "fetch", "origin", "main:refs/remotes/origin/main", "--depth=1"], check=False)

    # Try to find a common base commit
    result = subprocess.run(["git", "merge-base", "HEAD", "origin/main"], capture_output=True, text=True)
    base = result.stdout.strip()

    if not base:
        print("âš ï¸ No merge base found â€” assuming first PR. Scanning all files in src/.")
        files = list(SRC.rglob("*.py"))
    else:
        diff_output = subprocess.run(
            ["git", "diff", "--name-only", f"{base}...HEAD"],
            capture_output=True, text=True
        ).stdout.strip()
        files = [Path(line) for line in diff_output.splitlines() if line.endswith(".py")]

    # Filter only src/ files (not tests)
    files = [f for f in files if f.exists() and "tests" not in str(f) and str(f).startswith("src/")]
    print(f"ğŸ“‚ Changed files detected: {files}")
    return files

# === Generate tests with GitHub Copilot ===
def generate_tests_with_copilot(file_path: Path):
    prompt = f"Write runnable pytest test cases for {file_path}. Output only Python code."
    print(f"ğŸ§  Asking Copilot for tests for: {file_path}")

    # Detect which flag Copilot CLI supports (--prompt or -p)
    version_check = subprocess.run(
        ["gh", "copilot", "suggest", "--help"], capture_output=True, text=True
    )
    if "--prompt" in version_check.stdout:
        flag = "--prompt"
    else:
        flag = "-p"
    print(f"ğŸ” Detected Copilot CLI prompt flag: {flag}")

    # Run the copilot suggestion command safely
    result = sh(f'gh copilot suggest {flag} {json.dumps(prompt)} --limit 1', capture=True)

    # Clean up markdown formatting if Copilot returns code fences
    test_file = TESTS / f"test_{file_path.stem}.py"
    cleaned = result.strip()
    if cleaned.startswith("```"):
        cleaned = cleaned.replace("```python", "").replace("```", "")
    test_file.write_text(cleaned)
    print(f"âœ… Generated {test_file}")
    return test_file


# === Run pytest to validate generated tests ===
def run_pytest():
    print("ğŸ§ª Running pytest validation...")
    res = subprocess.run(["pytest", "-q", "--disable-warnings", "--maxfail=1"],
                         text=True, capture_output=True)
    print(res.stdout)
    if res.returncode != 0:
        print(res.stderr, file=sys.stderr)
    return res.returncode == 0

# === Commit & Push ===
def git_commit_and_push(files):
    sh('git config user.name "ci-bot"')
    sh('git config user.email "ci-bot@users.noreply.github.com"')
    for f in files:
        sh(f"git add {f}")
    sh('git commit -m "auto: add pytest files generated by Copilot" || true', check=False)
    sh('git push', check=False)
    print("ğŸš€ Committed and pushed generated tests.")

# === Rollback generated tests if validation fails ===
def rollback(files):
    for f in files:
        if f.exists():
            f.unlink()
            print(f"ğŸ§¹ Removed {f}")
    print("ğŸ§¹ Rolled back generated test files.")

# === Entry Point ===
if __name__ == "__main__":
    changed = get_changed_files()
    if not changed:
        print("â„¹ï¸ No changed Python files found.")
        sys.exit(0)

    generated = []
    for f in changed:
        generated.append(generate_tests_with_copilot(f))

    if run_pytest():
        print("âœ… Tests passed! Committing and pushing.")
        git_commit_and_push(generated)
    else:
        print("âŒ Tests failed. Cleaning up generated test files.")
        rollback(generated)
        sys.exit(1)
